import React, { Component } from 'react'

export class Login extends Component {
constructor(props) {
  super(props)

  this.state = {
     userName:"",
     password:""
  }
  this.handleInput=this.handleInput.bind(this);

}
handleInput=(event)=>{
const {name,value} =event.target;

}


  render() {
    return (
      <>
      <div>
          <form>
       <input placeholder='User Name' name='user'  onChange={this.handleInput}/>
       <input placeholder='Password' name='password' type="password" onChange={this.handleInput}/>

       <button >Login</button>
       </form>
      </div>
      </>
    )
  }
}

export default Login
