import React,{Component} from "react";

export default class List extends Component{
    constructor(props){
        super(props);
        console.log(this.props,"props...")
        this.state={
            editButtton:"Edit",
            deleteButton:"Delete",
          
        }
        this.onEdit=this.onEdit.bind(this)
    }
    onEdit=(index,item,value)=>{
      if(value=="Edit"){
          this.setState({
              editButtton:"Update",
              deleteButton:"Cancel"
          })
      }
      else{
        this.setState({
            editButtton:"Edit",
            deleteButton:"Delete",
        })
      }
    }
    render(){
        console.log(this.props)
        const {item,index}=this.props;
        return(
         <>
          <li className={index%2==0?'list-group-item even':'list-group-item odd'} key={index}>{item} <span><button className="btn btn-success ml-2" onClick={()=>this.onEdit(index,item,this.state.editButtton)}>{this.state.editButtton}</button></span><span><button className="btn btn-danger ml-2">{this.state.deleteButton}</button></span></li>
           
         </>
        );
    }
}


