import React, { Component } from 'react'
import './todolist.css';
export class Todolist extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            todoList: []
        }
        this.onChangeInput = this.onChangeInput.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.onFilter=this.onFilter.bind(this);

    }
    onChangeInput = (event) => {
        // console.log(event.target.value,"value....")
        this.setState({ name: event.target.value })
    }
    handleSubmit = () => {
        this.setState({ todoList: [...this.state.todoList, this.state.name] })
        this.setState({ name: "" })
        console.log(this.state.todoList, "todoList.....")

    }
    onFi
    render() {
        return (
            <div className='container-fluid'>

                <div className='row mt-3'>
                    <div className='col-md-6'>
                        <input className='form-control' onChange={this.onChangeInput} value={this.state.name} />
                    </div>
                    <div className='col-md-6'>
                        <button className='btn btn-primary' onClick={this.handleSubmit}>Submit</button>
                    </div>
                </div>
                <div className='row'>
                    <div className='col-md-12'>
                        {
                            this.state.todoList.length > 0 ?
                                <ul className='list-group col-md-8 mt-3'>
                                    {this.state.todoList.map(((item,index) =>
                                     <li key={index}  className={index%2==0?'list-group-item even':'list-group-item odd'}>{item}</li>
                                     )) }
                                </ul>
                                :
                                <div className="card mt-2 col-md-5">
                                    <div className="card-body">
                                       No Todo List Found
                                    </div>
                                </div>

                        }

                    </div>

                </div>

            </div>
        )
    }
}

export default Todolist
